import React, { useState } from 'react';
import './App.css';

function App() {
  const [image, setImage] = useState(null);
  const [musicUrl, setMusicUrl] = useState('');
  const [loading, setLoading] = useState(false);

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleGenerate = async () => {
    if (!image) {
      alert('Please upload an image first.');
      return;
    }

    const formData = new FormData();
    formData.append('image', image);

    try {
      setLoading(true);
      const response = await fetch('http://localhost:5000/api/generate-music', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      if (response.ok && data?.musicUrl) {
        setMusicUrl(`http://localhost:5000/${data.musicUrl}`);
      } else {
        alert('Something went wrong!');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Something went wrong!');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h1 className="title">AI Music Generator</h1>
      <p className="subtitle">Upload an image and let the music flow ðŸŽ¶</p>

      <input type="file" accept="image/*" onChange={handleImageChange} />
      <button onClick={handleGenerate} disabled={loading}>
        {loading ? 'Generating...' : 'Generate Music'}
      </button>

      {musicUrl && (
        <div className="music-player">
          <audio controls autoPlay>
            <source src={musicUrl} type="audio/mpeg" />
            Your browser does not support the audio element.
          </audio>
        </div>
      )}
    </div>
  );
}

export default App;
