import React, { useState } from 'react';
import './App.css';

function App() {
  const [image, setImage] = useState(null);
  const [loading, setLoading] = useState(false);
  const [musicUrl, setMusicUrl] = useState('');

  const handleFileChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleGenerate = async () => {
    if (!image) {
      alert("Please upload an image first.");
      return;
    }

    setLoading(true);

    const formData = new FormData();
    formData.append('image', image);

    try {
      const response = await fetch('http://localhost:5000/', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Music generation failed');
      }

      // Just use the sample song for now:
      setMusicUrl('http://localhost:5000/uploads/sample-music.mp3');
    } catch (error) {
      console.error('Error generating music:', error);
      alert('Something went wrong. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h1 className="heading">AI Music Generator</h1>
      <p className="subheading">Upload an image and let the music flow ðŸŽ¶</p>

      <input type="file" accept="image/*" onChange={handleFileChange} className="file-input" />

      <button className="generate-button" onClick={handleGenerate} disabled={loading}>
        {loading ? 'Generating...' : 'Generate Music'}
      </button>

      {musicUrl && (
        <div className="music-player">
          <audio controls src={musicUrl} autoPlay />
        </div>
      )}
    </div>
  );
}

export default App;
