import React from 'react';
import './App.css';

function App() {
  return (
    <>
      {/* Floating music icons */}
      <div>
        {[...Array(15)].map((_, i) => (
          <div
            key={i}
            className="music-icon"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 10}s`
            }}
          ></div>
        ))}
      </div>

      {/* Centered UI Container */}
      <div className="container">
        <h1>AI Music Generator</h1>
        <p>Generate music from your mood or an image ðŸŽ¼</p>

        {/* Upload button with popup text */}
        <div className="upload-wrapper">
          <label htmlFor="file-upload" className="upload-label">
            
            <span className="tooltip">Click to upload image</span>
          </label>
          <input
            type="file"
            id="file-upload"
            style={{ display: 'none' }}
            accept="image/*"
            onChange={(e) => {
              console.log("Uploaded:", e.target.files[0]);
            }}
          />
        </div>

        {/* Moved Generate button to bottom */}
        <button>Generate</button>
      </div>
    </>
  );
}

export default App;
