import { useEffect, useState } from "react";

function App() {
  const [message, setMessage] = useState("");

  useEffect(() => {
    fetch("/api/hello") // Adjust based on your backend routes
      .then((res) => res.json())
      .then((data) => setMessage(data.message));
  }, []);

  return <div>{message ? message : "Loading..."}</div>;
}

export default App;
