import React, { useState } from 'react';
import './App.css';

function App() {
  const [file, setFile] = useState(null);

  const handleFileChange = (e) => {
    const uploaded = e.target.files[0];
    if (uploaded) {
      setFile(uploaded);
      console.log("Selected file:", uploaded);
    }
  };

  const handleGenerate = async () => {
    if (!file) {
      alert("Please upload an image first.");
      return;
    }

    const formData = new FormData();
    formData.append("image", file);

    try {
      const response = await fetch("http://localhost:5000/generate-music", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      console.log("Music generated:", data);
      alert("Music generation successful!");
    } catch (error) {
      console.error("Error generating music:", error);
      alert("Failed to generate music.");
    }
  };

  return (
    <>
      {/* Floating music icons */}
      <div>
        {[...Array(15)].map((_, i) => (
          <div
            key={i}
            className="music-icon"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 10}s`,
            }}
          ></div>
        ))}
      </div>

      {/* UI */}
      <div className="container">
        <h1>AI Music Generator</h1>
        <p>Generate music from your mood or an image ðŸŽ¼</p>

        <div className="upload-wrapper">
          <label htmlFor="file-upload" className="upload-label">
            Upload your image
            <span className="tooltip">Click to upload image</span>
          </label>
          <input
            type="file"
            id="file-upload"
            style={{ display: 'none' }}
            accept="image/*"
            onChange={handleFileChange}
          />
        </div>

        <button onClick={handleGenerate}>Generate</button>
      </div>
    </>
  );
}

export default App;
