import React, { useState } from 'react';
import './App.css';

function App() {
  const [image, setImage] = useState(null);
  const [musicUrl, setMusicUrl] = useState('');
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
      console.log('Image uploaded:', file);
    }
  };

  const handleGenerate = async () => {
    if (!image) {
      alert('Please upload an image first.');
      return;
    }

    setLoading(true);
    const formData = new FormData();
    formData.append('image', image);

    try {
      const response = await fetch('http://localhost:5000/api/generate-music', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Server Error');
      }

      const data = await response.json();
      console.log('Response from server:', data);

      // Play the static audio file since weâ€™re not generating actual audio
      setMusicUrl('http://localhost:5000/uploads/sample-music.mp3');
    } catch (error) {
      console.error('Error generating music:', error);
      alert('Something went wrong while generating music.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>AI Music Generator</h1>
      <p>Upload an image and let the music flow ðŸŽ¶</p>

      <input type="file" accept="image/*" onChange={handleFileChange} />
      <br /><br />

      <button onClick={handleGenerate} disabled={loading}>
        {loading ? 'Generating...' : 'Generate Music'}
      </button>

      <br /><br />

      {musicUrl && (
        <div>
          <h3>Your Music:</h3>
          <audio controls src={musicUrl}></audio>
        </div>
      )}
    </div>
  );
}

export default App;
